import flaskimport osfrom flask import Flask, jsonify, requestimport jsonimport joblib as jlimport pandas as pdimport sysimport numpy as npsys.path.insert(0, 'src/')import configapp = Flask(__name__)def load_model():    model = jl.load(os.path.join('models/model_final.bin'))    return model@app.route('/predict', methods=['POST'])def predict():    input_json = json.dumps(request.get_json(), cls=NpEncoder)    data = pd.read_json(input_json)    model = load_model()    prediction = model.predict(data)[0]    description = None    if prediction == 1:        description = 'This individual has heart disease'    if prediction == 0:        description = 'This individual does not have heart disease'    response = json.dumps({'response': prediction, 'description': description}, cls=NpEncoder)    return response, 200class NpEncoder(json.JSONEncoder):    def default(self, obj):        if isinstance(obj, np.integer):            return int(obj)        if isinstance(obj, np.floating):            return float(obj)        if isinstance(obj, np.ndarray):            return obj.tolist()        return super(NpEncoder, self).default(obj)if __name__ == '__main__':    app.run(debug=True)